import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Récupérer le token depuis le localStorage
const tokenFromLocalStorage = localStorage.getItem('token');

// Thunk pour la connexion utilisateur
export const loginUser = createAsyncThunk(
  "auth/loginUser",
  async (userData, { rejectWithValue }) => {
    try {
      const response = await axios.post(
        "http://localhost:3001/api/v1/user/login",
        userData
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

// Thunk pour récupérer les données utilisateur
export const fetchUserProfile = createAsyncThunk(
  "auth/fetchUserProfile",
  async (_, { getState, rejectWithValue }) => {
    const { token } = getState().auth;
    try {
      const response = await axios.pg(
        "http://localhost:3001/api/v1/user/profile",
        {},
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const authSlice = createSlice({
  name: "auth",
  initialState: {
    user: null,
    token: tokenFromLocalStorage || null,
    status: "idle",
    error: null,
  },
  reducers: {
    logout(state) {
      state.user = null;
      state.token = null;
      state.status = "idle";
      state.error = null;
      // Supprimer le token du localStorage lors de la déconnexion
      localStorage.removeItem('token');
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(loginUser.fulfilled, (state, action) => {
        state.status = "succeeded";
        if (action.payload.body) {
          state.user = action.payload.body.user;
          state.token = action.payload.body.token;
          // Stocker le token dans le localStorage
          localStorage.setItem('token', state.token);
        }
      })
      .addCase(fetchUserProfile.fulfilled, (state, action) => {
        state.status = "succeeded";
        if (action.payload.body) {
          state.user = action.payload.body.user;
        }
      });
  },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
