import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../Features/Auth/AuthSlice";
import { useNavigate } from "react-router-dom";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, token, status, error } = useSelector((state) => state.auth);

  useEffect(() => {
    // Récupérer le token depuis localStorage si nécessaire
    const storedToken = localStorage.getItem("token");

    if (!storedToken) {
      navigate("/"); // Redirige vers la page d'accueil si non authentifié
    } else if (!token && storedToken) {
      dispatch(fetchUserProfile()); // Utiliser le token pour récupérer le profil
    }
  }, [dispatch, token, navigate]);

  if (status === "loading") {
    return <div>Loading...</div>;
  }

  if (status === "failed" && error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <main className="main bg-dark user-page">
      <UserHeader user={user} />
      <UserTransactions />
    </main>
  );
};

export default Profile;
