import React, { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { fetchUserProfile } from "../features/profile/profileSlice"; // Assurez-vous que cette action est définie
import Navbar from "../components/Navbar/Navbar";
import Footer from "../components/Footer/Footer";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

export default function Profile() {
  const { firstName } = useParams();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.profile.user);

  useEffect(() => {
    dispatch(fetchUserProfile({ firstName }));
  }, [dispatch, firstName]);

  if (!user) {
    return <p>Loading user profile...</p>;
  }

  return (
    <div>
      <Navbar />
      <main className="main bg-dark user-page">
        <UserHeader />
        <UserTransactions />
      </main>
      <Footer />
    </div>
  );
}
