import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateUser, fetchUserProfile } from "../../Features/Auth/AuthSlice";
import { useNavigate } from "react-router-dom";

const UserHeader = ({ user }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState(user.firstName || "");
  const [lastName, setLastName] = useState(user.lastName || "");
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { status } = useSelector((state) => state.auth);

  const handleSave = async () => {
    try {
      await dispatch(updateUser({ firstName, lastName })).unwrap();
      // Fetch updated user profile data
      await dispatch(fetchUserProfile()).unwrap();
      // Redirect to the profile page to force a refresh
      navigate(`/profile/${user._id}`, { replace: true });
    } catch (err) {
      console.error("Failed to update user:", err);
    }
  };

  return (
    <header className="header">
      {isEditing ? (
        <div className="edit-form">
          <input
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            placeholder="First Name"
          />
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            placeholder="Last Name"
          />
          <button onClick={handleSave} disabled={status === "loading"}>
            {status === "loading" ? "Saving..." : "Save"}
          </button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <h1>
            Welcome Back <br /> {`${user.firstName} ${user.lastName}`}
          </h1>
          <button className="edit-button" onClick={() => setIsEditing(true)}>
            Edit Name
          </button>
        </div>
      )}
    </header>
  );
};

export default UserHeader;
