import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { loginUser } from './AuthSlice';
import { useNavigate } from 'react-router-dom';

export default function SignIn() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { error, status } = useSelector((state) => state.auth);

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const result = await dispatch(loginUser({ email, password }));

    console.log("Résultat du dispatch:", result);

    if (loginUser.fulfilled.match(result)) {
      // Accès à l'ID utilisateur dans le payload
      const userId = result.payload?.body?.user?._id;
      if (userId) {
        console.log("ID utilisateur trouvé:", userId);
        navigate(`/profile/${userId}`);
      } else {
        console.error("L'ID utilisateur n'a pas été trouvé dans la réponse.");
      }
    } else {
      console.error("Le login n'a pas réussi.");
    }
  };

  return (
    <section className="sign-in-content">
      <i className="fa fa-user-circle sign-in-icon"></i>
      <h1>Sign In</h1>
      <form onSubmit={handleSubmit}>
        <div className="input-wrapper">
          <label htmlFor="username">Username</label>
          <input 
            type="text" 
            id="username" 
            value={email} 
            onChange={(e) => setEmail(e.target.value)} 
          />
        </div>
        <div className="input-wrapper">
          <label htmlFor="password">Password</label>
          <input 
            type="password" 
            id="password" 
            value={password} 
            onChange={(e) => setPassword(e.target.value)} 
          />
        </div>
        <div className="input-remember">
          <input type="checkbox" id="remember-me" />
          <label htmlFor="remember-me">Remember me</label>
        </div>
        <button className="sign-in-button" type="submit">
          Sign In
        </button>
      </form>
      {status === 'failed' && error && (
        <div className="error-message">
          {error.message ? error.message : 'An error occurred'}
        </div>
      )}
    </section>
  );
}
