import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Thunk pour la connexion utilisateur
export const loginUser = createAsyncThunk(
  "auth/loginUser",
  async (userData, { rejectWithValue }) => {
    try {
      const response = await axios.post(
        "http://localhost:3001/api/v1/user/login",
        userData
      );
      // Assurez-vous que la structure des données est correcte
      const { body } = response.data;
      localStorage.setItem('token', body.token); // Stocke le token
      return body; // Assurez-vous de retourner la structure correcte
    } catch (error) {
      return rejectWithValue(error.response ? error.response.data : { message: "Unknown error" });
    }
  }
);

// Thunk pour récupérer les données utilisateur
export const fetchUserProfile = createAsyncThunk(
  "auth/fetchUserProfile",
  async (_, { getState, rejectWithValue }) => {
    const { token } = getState().auth;
    try {
      const response = await axios.post(
        "http://localhost:3001/api/v1/user/profile",
        {}, // Corps de la requête POST est vide
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      return response.data.body; // Assurez-vous de retourner la structure correcte
    } catch (error) {
      return rejectWithValue(error.response ? error.response.data : { message: "Unknown error" });
    }
  }
);

const authSlice = createSlice({
  name: "auth",
  initialState: {
    user: null,
    token: localStorage.getItem('token') || null,
    status: "idle",
    error: null,
  },
  reducers: {
    logout(state) {
      state.user = null;
      state.token = null;
      state.status = "idle";
      state.error = null;
      localStorage.removeItem('token'); // Supprime le token du localStorage
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(loginUser.pending, (state) => {
        state.status = "loading";
      })
      .addCase(loginUser.fulfilled, (state, action) => {
        state.status = "succeeded";
        state.user = action.payload; // Assurez-vous de stocker l'utilisateur correctement
        state.token = action.payload.token; // Assurez-vous de stocker le token correctement
      })
      .addCase(loginUser.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      })
      .addCase(fetchUserProfile.pending, (state) => {
        state.status = "loading";
      })
      .addCase(fetchUserProfile.fulfilled, (state, action) => {
        state.status = "succeeded";
        state.user = action.payload; // Assurez-vous de mettre à jour l'utilisateur correctement
      })
      .addCase(fetchUserProfile.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      });
  },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
