import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile, updateUser } from "../Features/Auth/AuthSlice";
import { useNavigate, useParams } from "react-router-dom";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userId } = useParams();
  const { user, token, status, error } = useSelector((state) => state.auth);

  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState(user?.firstName || "");
  const [lastName, setLastName] = useState(user?.lastName || "");

  useEffect(() => {
    if (token && !user) {
      dispatch(fetchUserProfile());
    }
  }, [dispatch, token, user]);

  useEffect(() => {
    if (user && userId && userId !== user._id) {
      navigate("/404", { replace: true });
    }
  }, [user, userId, navigate]);

  useEffect(() => {
    if (user) {
      setFirstName(user.firstName);
      setLastName(user.lastName);
    }
  }, [user]);

  const handleSave = async () => {
    try {
      await dispatch(updateUser({ firstName, lastName }));
      // Appel pour récupérer les données mises à jour
      await dispatch(fetchUserProfile());
      setIsEditing(false); // Fermer le formulaire de modification après la sauvegarde
    } catch (err) {
      console.error("Failed to update user:", err);
    }
  };

  if (status === "loading") {
    return <div>Loading...</div>;
  }

  if (status === "failed" && error) {
    return <div>Error: {error.message}</div>;
  }

  if (!user) {
    return <div>No user data available</div>;
  }

  return (
    <main className="main bg-dark user-page">
      <UserHeader
        user={user}
        isEditing={isEditing}
        setIsEditing={setIsEditing}
        firstName={firstName}
        setFirstName={setFirstName}
        lastName={lastName}
        setLastName={setLastName}
        handleSave={handleSave}
      />
      <UserTransactions />
    </main>
  );
};

export default Profile;
