import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../Features/Auth/AuthSlice";
import { useNavigate, useParams } from "react-router-dom";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, token, status, error } = useSelector((state) => state.auth);
  const { userId } = useParams(); // Assumes URL has userId like /profile/:userId

  useEffect(() => {
    if (!token) {
      // Si le token n'existe pas, redirige vers la page d'accueil
      navigate("/");
    } else if (!user || (user && user._id !== userId)) {
      // Si les données utilisateur ne sont pas encore chargées ou si l'utilisateur connecté ne correspond pas à l'ID dans l'URL, fetch les données utilisateur
      dispatch(fetchUserProfile());
    }
  }, [dispatch, token, user, navigate, userId]);

  if (status === "loading") {
    return <div>Loading...</div>;
  }

  if (status === "failed" && error) {
    return <div>Error: {error.message}</div>;
  }

  // Assure que le profil est bien le bon utilisateur avant de rendre le composant
  if (user && user._id !== userId) {
    navigate("/"); // Redirige si l'utilisateur connecté n'est pas le même que celui dans l'URL
    return null; // Prévenir le rendu de la page
  }

  return (
    <main className="main bg-dark user-page">
      <UserHeader user={user} />
      <UserTransactions />
    </main>
  );
};

export default Profile;
