import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchUserProfile } from '../Features/Auth/AuthSlice';

const Profile = () => {
  const { id } = useParams(); // Récupère l'ID de l'utilisateur depuis l'URL
  const dispatch = useDispatch();
  const { user, token, status, error } = useSelector((state) => state.auth);

  useEffect(() => {
    // Assure que le profil est chargé uniquement si on est connecté (token est présent)
    if (token && !user) {
      console.log("Fetching user profile...");
      dispatch(fetchUserProfile());
    }
  }, [dispatch, token, user]);

  // Affiche une animation de chargement pendant que les données sont en cours de récupération
  if (status === 'loading') {
    return <div>Loading...</div>;
  }

  // Affiche les erreurs si la récupération des données échoue
  if (error) {
    return <div>Error: {error.message}</div>;
  }

  // Affiche les données utilisateur si elles sont disponibles
  return (
    <div>
      <h1>Profile</h1>
      {user ? (
        <div>
          <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
          <p><strong>Email:</strong> {user.email}</p>
          {/* Ajoutez d'autres informations utilisateur ici si nécessaire */}
        </div>
      ) : (
        <div>No user data available</div>
      )}
    </div>
  );
};

export default Profile;
