import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from "axios";

// Thunk for login
export const loginUser = createAsyncThunk(
  "auth/loginUser",
  async (credentials, { rejectWithValue }) => {
    try {
      const response = await axios.post("http://localhost:3001/api/v1/user/login", credentials);
      const { token } = response.data.body;
      localStorage.setItem("token", token); // Stockage du token dans le localStorage
      return response.data.body;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

// Thunk for fetchUserProfile
export const fetchUserProfile = createAsyncThunk(
  "auth/fetchUserProfile",
  async (_, { getState, rejectWithValue }) => {
    const { token } = getState().auth;
    try {
      const response = await axios.post("http://localhost:3001/api/v1/user/profile", {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

// Thunk pour mettre à jour l'utilisateur
export const updateUser = createAsyncThunk(
  "auth/updateUser",
  async (userData, { getState, dispatch, rejectWithValue }) => {
    const { token } = getState().auth;
    try {
      // Effectuer la mise à jour
      await axios.put("http://localhost:3001/api/v1/user/profile", userData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Récupérer les données mises à jour
      const response = await axios.post("http://localhost:3001/api/v1/user/profile", {
        headers: { Authorization: `Bearer ${token}` }
      });
      return response.data.body; // Assurez-vous que la réponse contient les données mises à jour
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

const authSlice = createSlice({
  name: "auth",
  initialState: {
    user: null,
    token: localStorage.getItem("token") || null,
    status: "idle",
    error: null,
  },
  reducers: {
    logout(state) {
      state.user = null;
      state.token = null;
      state.status = "idle";
      state.error = null;
      localStorage.removeItem("token");
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(loginUser.pending, (state) => {
        state.status = "loading";
      })
      .addCase(loginUser.fulfilled, (state, action) => {
        state.user = action.payload.body.user;
        state.token = action.payload.body.token;
        localStorage.setItem("token", state.token);
        state.status = "succeeded";
      })
      .addCase(loginUser.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      })
      .addCase(fetchUserProfile.pending, (state) => {
        state.status = "loading";
      })
      .addCase(fetchUserProfile.fulfilled, (state, action) => {
        state.user = action.payload.body.user;
        state.status = "succeeded";
      })
      .addCase(fetchUserProfile.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      })
      .addCase(updateUser.pending, (state) => {
        state.status = "loading";
      })
      .addCase(updateUser.fulfilled, (state, action) => {
        state.user = action.payload; // Mettre à jour l'état avec les données mises à jour
        state.status = "succeeded";
      })
      .addCase(updateUser.rejected, (state, action) => {
        state.status = "failed";
        state.error = action.payload;
      });
  },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
