import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../Features/Auth/AuthSlice";
import { useNavigate, useParams } from "react-router-dom";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { userId } = useParams(); // ID dans l'URL
  const { user, token, status, error } = useSelector((state) => state.auth);

  useEffect(() => {
    if (token && !user) {
      console.log("Fetching user profile...");
      dispatch(fetchUserProfile());
    }
  }, [dispatch, token, user]);

  useEffect(() => {
    // Vérifiez si les données de l'utilisateur sont disponibles avant de faire la vérification
    if (user && userId !== user._id) {
      // Vérifiez si l'URL contient des caractères supplémentaires après l'ID
      if (userId.length !== user._id.length) {
        navigate('/404', { replace: true });
      } else {
        navigate('/404', { replace: true });
      }
    }
  }, [user, userId, navigate]);

  if (status === "loading") {
    return <div>Loading...</div>;
  }

  if (status === "failed" && error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <main className="main bg-dark user-page">
      <UserHeader user={user} />
      <UserTransactions />
    </main>
  );
};

export default Profile;
