import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

export const loginUser = createAsyncThunk(
  'auth/loginUser',
  async (userData, { rejectWithValue }) => {
    console.log('User data sent to API:', userData); // Vérifie les données envoyées
    try {
      const response = await axios.post('http://localhost:3001/api/v1/user/login', userData);
      console.log('Response from login API:', response.data); 
      return response.data;
    } catch (error) {
      console.error('Error in loginUser thunk:', error.response.data); 
      return rejectWithValue(error.response.data);
    }
  }
);


const authSlice = createSlice({
  name: 'auth',
  initialState: {
    user: null,
    token: null,
    status: 'idle',
    error: null,
  },
  reducers: {
    logout(state) {
      state.user = null;
      state.token = null;
      state.status = 'idle';
      state.error = null;
      console.log('User logged out. State reset to initial values.');
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(loginUser.pending, (state) => {
        state.status = 'loading';
        console.log('Login request pending...');
      })
      .addCase(loginUser.fulfilled, (state, action) => {
        console.log('Full response payload:', action.payload); // Affiche le payload complet
        state.status = 'succeeded';

        // Vérifie que les données existent et sont bien structurées
        if (action.payload.body) {
          state.user = action.payload.body.user;
          state.token = action.payload.body.token;
          console.log('Login succeeded. User data:', state.user); 
          console.log('Token received:', state.token); 
        } else {
          console.error('Unexpected response structure:', action.payload);
        }
      });

      addCase(loginUser.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
        console.error('Login failed with error:', state.error); // Affiche les détails de l'erreur
      });
  },
});

export const { logout } = authSlice.actions;
export default authSlice.reducer;
