import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { fetchUserProfile } from "../Features/Auth/AuthSlice";
import { useNavigate } from "react-router-dom";
import UserHeader from "../components/User-Header/UserHeader";
import UserTransactions from "../components/User-Transactions/UserTransactions";

const Profile = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user, token, status, error } = useSelector((state) => state.auth);

  useEffect(() => {
    if (!user && !token) {
      navigate("/login"); // Redirige vers la page de connexion si non authentifié
    } else if (token && !user) {
      dispatch(fetchUserProfile());
    }
  }, [dispatch, token, user, navigate]);

  if (status === "loading") {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <>
      <main className="main bg-dark user-page">
        <UserHeader user={user}/>
        <UserTransactions />
      </main>
    </>
  );
};

export default Profile;
