import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { updateUser, fetchUserProfile } from "../../Features/Auth/AuthSlice";

const UserHeader = ({ user }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState(user.firstName || "");
  const [lastName, setLastName] = useState(user.lastName Â  
 || "");
  const [updateStatus, setUpdateStatus] = useState("idle"); // New state for update status
  const dispatch = useDispatch();

  const handleSave = async () => {
    try {
      setUpdateStatus("loading"); // Set loading state
      await dispatch(updateUser({ firstName, lastName }));
      setFirstName(firstName);
      setLastName(lastName);
      await dispatch(fetchUserProfile());
      setIsEditing(false);
      setUpdateStatus("idle"); // Reset update state
    } catch (err) {
      console.error("Failed to update user:", err);
      setUpdateStatus("failed"); // Set error state (optional)
    }
  };

  if (updateStatus === "loading") {
    return <div>Updating profile...</div>;
  }


  return (
    <header className="header">
      {isEditing ? (
        <div className="edit-form">
          <input
            type="text"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            placeholder="First Name"
          />
          <input
            type="text"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            placeholder="Last Name"
          />
          <button onClick={handleSave}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div>
          <h1>
            Welcome Back <br /> {`${user.firstName} ${user.lastName}`}
          </h1>
          <button className="edit-button" onClick={() => setIsEditing(true)}>
            Edit Name
          </button>
        </div>
      )}
    </header>
  );
};

export default UserHeader;
