import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { updateUser, fetchUpdatedProfile } from '../../Features/Auth/AuthSlice';

const UserHeader = ({ user }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [firstName, setFirstName] = useState(user.firstName || '');
  const [lastName, setLastName] = useState(user.lastName || '');
  const [updateStatus, setUpdateStatus] = useState('idle'); // État de chargement
  const dispatch = useDispatch();

  const handleSave = async () => {
    try {
      setUpdateStatus('loading');
      await dispatch(updateUser({ firstName, lastName }));
      // Appel du thunk pour récupérer les données mises à jour
      await dispatch(fetchUpdatedProfile());
      setFirstName(firstName);
      setLastName(lastName);
      setIsEditing(false);
      setUpdateStatus('idle');
    } catch (err) {
      console.error('Échec de la mise à jour du profil :', err);
      setUpdateStatus('failed'); // État d'erreur
    }
  };

  return (
    <header className="header">
      {updateStatus === 'loading' ? (
        <div>Mise à jour du profil...</div>
      ) : (
        <>
          {isEditing ? (
            <div className="edit-form">
              {/* Formulaire d'édition */}
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="Prénom"
              />
              <input
                type="text"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                placeholder="Nom"
              />
              <button onClick={handleSave}>Enregistrer</button>
              <button onClick={() => setIsEditing(false)}>Annuler</button>
            </div>
          ) : (
            <div>
              <h1>
                Bienvenue ! <br /> {`${user.firstName} ${user.lastName}`}
              </h1>
              <button className="edit-button" onClick={() => setIsEditing(true)}>
                Modifier le nom
              </button>
            </div>
          )}
        </>
      )}
    </header>
  );
};

export default UserHeader;
